import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io.connect('http://localhost:3001');

function App() {

  const [message, setMessage] = useState('');

  const [messageList, setMessageList] = useState([]);

  const [username, setUsername] = useState('');

  const [joined, setJoined] = useState(false);


  const sendMessage = () => {
    if (message.trim() !== '') { 
      const messageData = {
        author: username, 
        message: message,
        time: new Date(Date.now()).getHours() + ':' + new Date(Date.now()).getMinutes(),
      };

      socket.emit('send_message', messageData);
      setMessage(''); 
    }
  };


  useEffect(() => {

    socket.on('receive_message', (data) => {

      setMessageList((list) => [...list, data]);
    });


    return () => {
      socket.off('receive_message');
    };
  }, []); 


  const joinChat = () => {
    if (username.trim() !== '') { 
      setJoined(true); 
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100 font-inter p-4">
      {!joined ? (

        <div className="flex flex-col items-center p-8 bg-white rounded-xl shadow-lg w-full max-w-md">
          <h1 className="text-3xl font-bold text-gray-800 mb-6">Join Chat</h1>
          <input
            type="text"
            placeholder="Your Name..."
            className="w-full p-3 mb-4 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            onChange={(event) => setUsername(event.target.value)} 
            onKeyPress={(event) => {
              if (event.key === 'Enter') { 
                joinChat();
              }
            }}
          />
          <button
            onClick={joinChat} 
            className="w-full bg-blue-600 text-white py-3 rounded-lg hover:bg-blue-700 transition duration-300 ease-in-out shadow-md"
          >
            Join Chat
          </button>
        </div>
      ) : (

        <div className="flex flex-col bg-white rounded-xl shadow-lg w-full max-w-lg h-[80vh] overflow-hidden">
          {/* Chat Header */}
          <div className="bg-blue-600 text-white p-4 rounded-t-xl flex justify-between items-center shadow-md">
            <h2 className="text-xl font-semibold">Live Chat</h2>
            <span className="text-sm">Logged in as: <span className="font-bold">{username}</span></span>
          </div>

          {/* Chat Body (Message Display Area) */}
          <div className="flex-1 p-4 overflow-y-auto custom-scrollbar">
            {messageList.map((msg, index) => ( 
              <div
                key={index} 
                className={`flex mb-3 ${msg.author === username ? 'justify-end' : 'justify-start'}`} 
              >
                <div
                  className={`max-w-[70%] p-3 rounded-lg shadow-sm ${
                    msg.author === username
                      ? 'bg-blue-500 text-white rounded-br-none'
                      : 'bg-gray-200 text-gray-800 rounded-bl-none'
                  }`}
                >
                  <div className="font-bold text-sm mb-1">
                    {msg.author === username ? 'You' : msg.author} {/* Display 'You' for own messages. */}
                  </div>
                  <div className="text-base break-words">{msg.message}</div>
                  <div className={`text-xs mt-1 ${msg.author === username ? 'text-blue-200' : 'text-gray-500'} text-right`}>
                    {msg.time} {/* Display message timestamp. */}
                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* Chat Footer (Message Input Area) */}
          <div className="p-4 border-t border-gray-200 flex items-center bg-gray-50 rounded-b-xl shadow-inner">
            <input
              type="text"
              placeholder="Type your message..."
              className="flex-1 p-3 border border-gray-300 rounded-l-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={message} 
              onChange={(event) => setMessage(event.target.value)} 
              onKeyPress={(event) => {
                if (event.key === 'Enter') { 
                  sendMessage();
                }
              }}
            />
            <button
              onClick={sendMessage} 
              className="bg-blue-600 text-white p-3 rounded-r-lg hover:bg-blue-700 transition duration-300 ease-in-out flex items-center justify-center shadow-md"
            >
              {/* Lucide React Send icon (SVG for visual appeal) */}
              <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="lucide lucide-send">
                <path d="m22 2-7 20-4-9-9-4 20-7Z"/><path d="M15 7L7 15"/>
              </svg>
            </button>
          </div>
        </div>
      )}
      {/* Tailwind CSS Script for styling */}
      <script src="https://cdn.tailwindcss.com"></script>
      {/* Custom CSS for scrollbar (optional, but improves aesthetics) */}
      <style>
        {`
        @import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&display=swap');
        body {
          font-family: 'Inter', sans-serif;
        }
        .custom-scrollbar::-webkit-scrollbar {
          width: 8px;
        }
        .custom-scrollbar::-webkit-scrollbar-track {
          background: #f1f1f1;
          border-radius: 10px;
        }
        .custom-scrollbar::-webkit-scrollbar-thumb {
          background: #888;
          border-radius: 10px;
        }
        .custom-scrollbar::-webkit-scrollbar-thumb:hover {
          background: #555;
        }
        `}
      </style>
    </div>
  );
}

export default App;
